import React, { Component, Fragment } from 'react'
import axios from 'axios'
import config from './constant'
import Suggestion from './suggestion'
import {InputGroup , Dropdown ,FormControl} from 'react-bootstrap'


class Home extends Component {
    state = {
        search: '',
        suggestions: [],
        showSuggestion: false,
        data: {}
    }

    showDetails = (id) => {
        axios.get(`${config.baseUrl}/details/?id=${id}`)
            .then(res => {
                if (res.data) {
                    this.setState({
                        ...this.state,
                        data: res.data
                    })
                }
            })
            .catch(err => console.log(err))
    }

    querySearch = (event) => {
        if (this.input.value.length > 2) {
            this.setState({
                ...this.state,
                search: this.input.value
            })
        }


        if (this.state.search.length > 1) {
            axios.get(`${config.baseUrl}/search/?name=${this.state.search}`)
                .then(res => {
                    console.log(res.data)
                    this.setState({
                        ...this.state,
                        suggestions: res.data,
                        showSuggestion: true
                    })

                })
                .catch(err => console.log(err))
        }
    }
    render() {
        console.log(this.state.suggestions, "inrender")
        return (
            <Fragment>
                <div>
                <InputGroup>
                    <InputGroup.Prepend>
                    <InputGroup.Text>With textarea</InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl as="textarea" aria-label="With textarea" />
                </InputGroup>
                    <input type='text'
                        ref={(input) => this.input = input}
                        onChange={(event) => this.querySearch(event)}
                        name="battleSearch"
                        placeholder='Search Battles' />
                </div>

                    {this.state.showSuggestion &&
                        <Suggestion showDetails={this.showDetails} results={this.state.suggestions} />}

            </Fragment>
        )
    }
}

export default Home;
