import React, { Component, Fragment } from 'react'
import axios from 'axios'
import config from './constant'
import BattleDetails from './battleDetails'
import { AutoComplete ,Layout } from 'antd';

const { Header, Content } = Layout;

const { Option } = AutoComplete

class Home extends Component {
    state = {
        search: '',
        suggestions: [],
        showSuggestion: false,
        data: [],
        showData: false
    }

    showDetails = (name) => {
        axios.get(`${config.baseUrl}/details/?name=${name}`)
            .then(res => {
                if (res.data) {
                    this.setState({
                        ...this.state,
                        data: res.data.data,
                        showData:true
                    })
                }
            })
            .catch(err => console.log(err))
    }

    onChange = (event) => {
        if (event.length > 2) {
            this.setState({
                ...this.state,
                search: event
            })
        }

        if (this.state.search.length > 2) {
            axios.get(`${config.baseUrl}/search/?name=${this.state.search}`)
                .then(res => {
                    console.log(res.data.data)

                    this.setState({
                        ...this.state,
                        suggestions: res.data.data,
                        showSuggestion: true
                    })

                })
                .catch(err => console.log(err))
        }
    }

    render() {
        return (
            <Fragment>
                  <Layout>
                    <Header>
                        <AutoComplete 
                            style={{
                            'width': '100%'
                            }}
                            onSelect = {this.showDetails}
                            onChange={this.onChange}
                            placeholder="Search Battles here..."
                        >

                            {this.state.showSuggestion && this.state.suggestions.map(item => (
                                    <Option key={item.id}  value={item.name}>
                                    {item.name}
                                    </Option>
                                ))}

                        </AutoComplete>
                    </Header>
                    <Layout>
                        {this.state.showData && <Content><BattleDetails details = {this.state.data}/></Content>}
                    </Layout>
                    </Layout>
            </Fragment>
        )
    }
}

export default Home;
