import React, { Component, Fragment } from 'react'
import axios from 'axios'
import config from './constant'
import Suggestion from './suggestion'
import { AutoComplete } from 'antd';

const { Option } = AutoComplete

class Home extends Component {
    state = {
        search: '',
        suggestions: [],
        showSuggestion: false,
        data: {}
    }

    showDetails = (name) => {
        axios.get(`${config.baseUrl}/details/?name=${name}`)
            .then(res => {
                if (res.data) {
                    this.setState({
                        ...this.state,
                        data: res.data
                    })
                }
            })
            .catch(err => console.log(err))
    }

    onSelect = (data) => {
        console.log(data , 'in onselect')
    }

    onSearch = (data) => {
        console.log(data, "in onsearch")
    }

    onChange = (event) => {
        if (event.length > 2) {
            this.setState({
                ...this.state,
                search: event
            })
        }


        if (this.state.search.length > 1) {
            axios.get(`${config.baseUrl}/search/?name=${this.state.search}`)
                .then(res => {
                    console.log(res.data.data)

                    this.setState({
                        ...this.state,
                        suggestions: res.data.data,
                        showSuggestion: true
                    })

                })
                .catch(err => console.log(err))
        }
    }

    // querySearch = (event) => {
    //     if (event.length > 2) {
    //         this.setState({
    //             ...this.state,
    //             search: event
    //         })
    //     }


    //     if (this.state.search.length > 1) {
    //         axios.get(`${config.baseUrl}/search/?name=${this.state.search}`)
    //             .then(res => {
    //                 console.log(res.data)
    //                 this.setState({
    //                     ...this.state,
    //                     suggestions: res.data,
    //                     showSuggestion: true
    //                 })

    //             })
    //             .catch(err => console.log(err))
    //     }
    // }
    render() {
        console.log(this.state.data, "inrender")
        return (
            <Fragment>
                <div>   
                    <AutoComplete

                        style={{
                        'width': '100%'
                        }}
                        onSelect = {this.showDetails}
                        onSearch={this.onSearch}
                        onChange={this.onChange}
                        placeholder="control mode"
                    >

                        {this.state.showSuggestion && this.state.suggestions.map(item => (
                                <Option key={item.id}  value={item.name}>
                                {item.name}
                                </Option>
                            ))}

                    </AutoComplete>


                        {/* <input type='text'
                            ref={(input) => this.input = input}
                            onChange={(event) => this.querySearch(event)}
                            name="battleSearch"
                            placeholder='Search Battles' /> */}
                </div>

                    {/* {this.state.showSuggestion &&
                        <Suggestion showDetails={this.showDetails} results={this.state.suggestions} />} */}

            </Fragment>
        )
    }
}

export default Home;
