import React, { Component, Fragment } from 'react'
import axios from 'axios'
import config from './constant'
import Suggestion from './suggestion'
import { AutoComplete } from 'antd';



class Home extends Component {
    state = {
        search: '',
        suggestions: [],
        showSuggestion: false,
        data: {}
    }

    showDetails = (id) => {
        axios.get(`${config.baseUrl}/details/?id=${id}`)
            .then(res => {
                if (res.data) {
                    this.setState({
                        ...this.state,
                        data: res.data
                    })
                }
            })
            .catch(err => console.log(err))
    }

    onSelect = (data) => {
        console.log(data , 'in onselect')
    }

    onSearch = (datd) => {
        console.log(data, "in onsearch")
    }

    onChange = (data) => {
        console.log(data , "onchcnafe")
    }

    querySearch = (event) => {
        if (this.input.value.length > 2) {
            this.setState({
                ...this.state,
                search: this.input.value
            })
        }


        if (this.state.search.length > 1) {
            axios.get(`${config.baseUrl}/search/?name=${this.state.search}`)
                .then(res => {
                    console.log(res.data)
                    this.setState({
                        ...this.state,
                        suggestions: res.data,
                        showSuggestion: true
                    })

                })
                .catch(err => console.log(err))
        }
    }
    render() {
        console.log(this.state.suggestions, "inrender")
        return (
            <Fragment>
                <div>   
                    <AutoComplete
                        value={0}
                        options={this.state.suggestions}
                        style={{
                        width: 200,
                        }}
                        onSelect={onSelect}
                        onSearch={onSearch}
                        onChange={onChange}
                        placeholder="control mode"
                    />


                        <input type='text'
                            ref={(input) => this.input = input}
                            onChange={(event) => this.querySearch(event)}
                            name="battleSearch"
                            placeholder='Search Battles' />
                </div>

                    {/* {this.state.showSuggestion &&
                        <Suggestion showDetails={this.showDetails} results={this.state.suggestions} />} */}

            </Fragment>
        )
    }
}

export default Home;
